
$(function () {
  $(window).scroll(function () {

    var scrollTop = $(this).scrollTop();
    
    if (scrollTop != 0) {
    console.log(scrollTop);

      $(".ce").stop().animate({
        'opacity': '0.8'
      }, 400);
        $("#navce").css("padding", "10px 10px");
        $("#logo").css("fontsize", "20px ");
      
    } else{
      $(".ce").stop().animate({
        'opacity': '1'
      }, 400);
      $("#navce").css("padding", "30px 10px");
      $("#logo").css("fontsize", "25px ");
    }
      
  });

  $(".ce").hover(
    function (e) {
      var scrollTop = $(window).scrollTop();
      if (scrollTop != 0) {
        $(".ce").stop().animate({
          'opacity': '1'
        }, 400);
      }
    },
    function (e) {
      var scrollTop = $(window).scrollTop();
      if (scrollTop != 0) {
        $(".ce").stop().animate({
          'opacity': '0.8'
        }, 400);
      }
    }
  );
});

